#!/bin/bash

# Cleanup script to remove all Stremio addon files from Raspberry Pi
# This includes: services, directories, Nginx configs, SSL certificates, backups, and logs

set -e

echo "๐งน Starting cleanup of all Stremio addon files from Raspberry Pi..."
echo ""

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

# Function to run commands with sudo
run_sudo() {
    echo -e "${YELLOW}Running:${NC} sudo $*"
    sudo "$@"
}

# Function to check if a path exists and remove it
remove_if_exists() {
    if [ -e "$1" ] || [ -d "$1" ]; then
        echo -e "${YELLOW}Removing:${NC} $1"
        run_sudo rm -rf "$1"
        echo -e "${GREEN}โ Removed${NC}"
    else
        echo -e "${YELLOW}Not found (skipping):${NC} $1"
    fi
}

# Step 1: Stop and disable all stremio-addon services
echo "๐ฆ Step 1: Stopping and disabling services..."
for service_file in /etc/systemd/system/stremio-addon*.service; do
    if [ -f "$service_file" ]; then
        service_name=$(basename "$service_file" .service)
        echo -e "${YELLOW}Stopping service:${NC} $service_name"
        run_sudo systemctl stop "$service_name" 2>/dev/null || true
        run_sudo systemctl disable "$service_name" 2>/dev/null || true
        echo -e "${GREEN}โ Service stopped and disabled${NC}"
    fi
done

# Reload systemd
echo -e "${YELLOW}Reloading systemd daemon...${NC}"
run_sudo systemctl daemon-reload
echo -e "${GREEN}โ Systemd reloaded${NC}"
echo ""

# Step 2: Remove systemd service files
echo "๐ฆ Step 2: Removing systemd service files..."
for service_file in /etc/systemd/system/stremio-addon*.service; do
    remove_if_exists "$service_file"
done
echo ""

# Step 3: Remove addon directories
echo "๐ Step 3: Removing addon directories..."
for addon_dir in /opt/stremio-addon*; do
    remove_if_exists "$addon_dir"
done
echo ""

# Step 4: Remove Nginx configurations
echo "๐ Step 4: Removing Nginx configurations..."
for nginx_config in /etc/nginx/sites-available/stremio-addon*; do
    if [ -f "$nginx_config" ]; then
        config_name=$(basename "$nginx_config")
        # Remove symlink from sites-enabled
        remove_if_exists "/etc/nginx/sites-enabled/$config_name"
        # Remove config file
        remove_if_exists "$nginx_config"
    fi
done

# Test and reload Nginx
echo -e "${YELLOW}Testing Nginx configuration...${NC}"
if run_sudo nginx -t 2>/dev/null; then
    echo -e "${YELLOW}Reloading Nginx...${NC}"
    run_sudo systemctl reload nginx
    echo -e "${GREEN}โ Nginx reloaded${NC}"
else
    echo -e "${RED}โ Nginx config test failed, but continuing...${NC}"
fi
echo ""

# Step 5: Remove SSL certificates (Let's Encrypt)
echo "๐ Step 5: Removing SSL certificates..."
# Find all domains that might have certificates
for domain_dir in /etc/letsencrypt/live/*; do
    if [ -d "$domain_dir" ]; then
        domain=$(basename "$domain_dir")
        # Check if this domain looks like a stremio addon domain
        if [[ "$domain" == *"stremio"* ]] || [[ "$domain" == *"duckdns"* ]]; then
            echo -e "${YELLOW}Removing SSL certificate for:${NC} $domain"
            # Use certbot to revoke and delete (non-interactive)
            run_sudo certbot delete --cert-name "$domain" --non-interactive 2>/dev/null || {
                # If certbot delete fails, manually remove
                echo -e "${YELLOW}Certbot delete failed, manually removing...${NC}"
                remove_if_exists "/etc/letsencrypt/live/$domain"
                remove_if_exists "/etc/letsencrypt/archive/$domain"
                remove_if_exists "/etc/letsencrypt/renewal/$domain.conf"
            }
            echo -e "${GREEN}โ Certificate removed${NC}"
        fi
    fi
done
echo ""

# Step 6: Remove backup directories
echo "๐พ Step 6: Removing backup directories..."
for backup_dir in /var/backups/stremio-addon*; do
    remove_if_exists "$backup_dir"
done
echo ""

# Step 7: Remove log directories
echo "๐ Step 7: Removing log directories..."
for log_dir in /var/log/stremio-addon*; do
    remove_if_exists "$log_dir"
done
echo ""

# Step 8: Remove temporary installation files
echo "๐๏ธ  Step 8: Cleaning up temporary files..."
for temp_dir in /tmp/stremio-addon-install.*; do
    if [ -d "$temp_dir" ]; then
        remove_if_exists "$temp_dir"
    fi
done
for temp_file in /tmp/stremio-addon*.service.* /tmp/server.js.*; do
    if [ -f "$temp_file" ]; then
        remove_if_exists "$temp_file"
    fi
done
echo ""

# Step 9: Clean up any remaining processes
echo "๐ Step 9: Checking for remaining processes..."
pids=$(pgrep -f "stremio-addon" || true)
if [ -n "$pids" ]; then
    echo -e "${YELLOW}Found running processes, killing them...${NC}"
    echo "$pids" | xargs sudo kill -9 2>/dev/null || true
    echo -e "${GREEN}โ Processes killed${NC}"
else
    echo -e "${GREEN}โ No running processes found${NC}"
fi
echo ""

# Final summary
echo "โโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโโ